const links = [
  "https://pay.cakto.com.br/yprdmik_624343",
  "https://pay.cakto.com.br/3bhwur2_624356",
  "https://pay.cakto.com.br/37qb9pb_624361"
];

let resultado = false;

for (let i = 0; i < items.length; i++) {
  const type = items[i].json.type || '';
  const conteudo = items[i].json.conteudo || '';
  if (
